#include "stdlib/stats/base/dists/signrank/cdf.h"
#include "stdlib/math/base/assert/is_positive_integer.h"
#include "stdlib/math/base/assert/is_finite.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/round.h"
#include "stdlib/math/base/special/exp.h"
#include "stdlib/constants/float64/ln_two.h"

/**
* Calculates the weight for the `(x,n)` pair.
*
* @private
* @param x  input value
* @param n  number of observations
* @return   weight
*/
static double weights( double x, const double n ) {
	int mlim;

	if ( n == 0 ) {
		return ( x == 0 ) ? 1 : 0;
	}
	mlim = n * ( n + 1 ) / 2;
	if (
		x < 0 ||
		x > mlim
	) {
		return 0;
	}
	if ( x > mlim / 2 ) {
		x = mlim - x;
	}
	return weights( x - n, n - 1 ) + weights( x, n - 1 );
}

/**
* Evaluates the cumulative distribution function (CDF) of the Wilcoxon signed rank test statistic with `n` observations.
*
* @param x  input value
* @param n  number of observations
* @return   evaluated CDF
*
* @example
* var y = cdf( 7.0, 9 );
* // returns ~0.037
*/
double stdlib_base_dists_signrank_cdf( double x, const double n ) {
	int mlim;
	double pui;
	int i;
	double p;
	if (
		stdlib_base_is_nan( x ) ||
		!stdlib_base_is_positive_integer( n ) ||
		!stdlib_base_is_finite( n )
	) {
		return 0.0/0.0; // NaN
	}
	if ( x < 0.0 ) {
		return 0.0;
	}
	x = stdlib_base_round( x );
	mlim = n * ( n + 1 ) / 2;
	if ( x >= mlim ) {
		return 1.0;
	}
	pui = stdlib_base_exp( -n * STDLIB_CONSTANT_FLOAT64_LN2 );
	p = 0;
	for ( i = 0; i <= x; i++ ) {
		p += weights( i, n ) * pui;
	}
	return p;
}
